import { createContext, useContext, useState } from 'react';
import { Navigate, useNavigate } from 'react-router-dom';

const userAuthContext = createContext();

export function UserAuthContextProvider({ children }) {
    const [user, setUser] = useState({});
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate()
    const login = async (email, password) => {
        try {
          const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/login`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, password }),
          });
      
          const data = await response.json();
      
          if (response.status === 201) {
            // Handle the 201 status code as an error
            console.error('Error: Unexpected HTTP status code 201');
            // Show an error message to the user or perform other error handling
          } else {
            localStorage.setItem('access', data.results.token);
            setUser(data.results.verifyAdmin);
            setIsLoggedIn(true);
            navigate('/dashboard');
            console.log('API Response:', data);
          }
        } catch (error) {
          console.error('Error during API request:', error);
          // Handle other errors, e.g., show a generic error message to the user
        }
      };
          const forgotPassword = () => {
        // Implement the logic for forgot password if needed
    };

    return (
        <userAuthContext.Provider value={{ login, forgotPassword, user, isLoggedIn }}>
            {children}
        </userAuthContext.Provider>
    );
}

export function useUserAuth() {
    return useContext(userAuthContext);
}
