import React, { useState, useEffect } from 'react';
import DashboardNavbar from './DashboardNavbar';

const PromoCodeManagement = () => {
  const [data, setData] = useState([]);
  const [promoCodeForm, setPromoCodeForm] = useState({
    name: '',
    validFrom: '',
    validTo: '',
    discount: '',
  });
  const apiBaseUrl = 'http://ec2-52-66-186-107.ap-south-1.compute.amazonaws.com:3009';

  useEffect(() => {
    const fetchPromocode = async () => {
      try {
        const authToken = localStorage.getItem('access');
        if (!authToken) {
          throw new Error('Authentication token not found');
        }

        const headers = {
          'x-auth-token-admin': authToken,
        };

        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/getAllPromocodes`, {
          method: 'POST',
          headers: {
            ...headers,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const userData = await response.json();
        setData(userData.results.promocodes);
      } catch (error) {
        console.error('Error fetching promo codes:', error);
        // Handle errors accordingly
      }
    };

    fetchPromocode();
  }, []);

  const formatDate = (dateString) => {
    const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
    return new Date(dateString).toLocaleDateString('en-GB', options);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPromoCodeForm({
      ...promoCodeForm,
      [name]: value,
    });
  };

  const addPromocode = async (e) => {
    e.preventDefault();

    try {
      const authToken = localStorage.getItem('access');
      if (!authToken) {
        throw new Error('Authentication token not found');
      }

      // Validate the "validFrom" field
      if (!promoCodeForm.validFrom) {
        throw new Error('Please provide a valid start date');
      }

      // Format the date fields as "YYYY-MM-DD"
      const formattedvalidFrom = new Date(promoCodeForm.validFrom).toISOString().split('T')[0];
      const formattedvalidTo = promoCodeForm.validTo
        ? new Date(promoCodeForm.validTo).toISOString().split('T')[0]
        : null;

      console.log('Formatted Start Date:', formattedvalidFrom);
      console.log('Formatted End Date:', formattedvalidTo);

      const headers = {
        'x-auth-token-admin': authToken,
        'Content-Type': 'application/json',
      };

      const requestBody = {
        ...promoCodeForm,
        validFrom: formattedvalidFrom,
        validTo: formattedvalidTo,
      };

      console.log('Request Body:', requestBody);

      const response = await fetch(`${apiBaseUrl}/api/admin/addPromocode`, {
        method: 'POST',
        headers,
        body: JSON.stringify(requestBody),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();
      console.log('API Response:', result);

      // Optionally, update the data state with the new promo code
      setData([...data, result.results.promocode]);

      // Clear the form fields
      setPromoCodeForm({
        name: '',
        validFrom: '',
        validTo: '',
        discount: '',
      });
    } catch (error) {
      console.error('Error adding promo code:', error.message);
      if (error.response) {
        // The request was made, but the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Server Error Data:', error.response.data);
        console.error('Server Error Status:', error.response.status);
        console.error('Server Error Headers:', error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        console.error('No Response Received');
        console.error('Request Data:', error.request);
      } else {
        // Something happened in setting up the request that triggered an Error
        console.error('Error Message:', error.message);
      }
      // Handle errors accordingly, e.g., display an error message to the user
    }
      };

  return (
    <>
      <div className='admin_main'>
        <DashboardNavbar />
        <div className="admin_panel_data height_adjust">
          <div className="row mx-0">
            <div className="col-12 design_outter_comman shadow mb-4">
              <div className="row comman_header justify-content-between">
                <div className="col">
                  <h2>Add Promo Code</h2>
                </div>
              </div>
              <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" onSubmit={addPromocode}>
                <div className="form-group col-4">
                  <label htmlFor="promoCode">Promo Code</label>
                  <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={promoCodeForm.name}
                    onChange={handleInputChange}
                  />
                </div>
                <div className="form-group col-8">
                  <div className="row align-items-end">
                    {/* Additional form fields */}
                    <div className="form-group mb-0 col-6">
                      <label htmlFor="validFrom">Start Date</label>
                      <input
                        type="date"
                        className="form-control"
                        value={promoCodeForm.validFrom}
                        onChange={handleInputChange}
                        name="validFrom"
                      />
                    </div>
                    <div className="form-group mb-0 col-6">
                      <label htmlFor="validTo">End Date</label>
                      <input
                        type="date"
                        className="form-control"
                        value={promoCodeForm.validTo}
                        onChange={handleInputChange}
                        name="validTo"
                      />
                    </div>
                  </div>
                </div>
                <div className="form-group mb-0 col">
                  <label htmlFor="amount">Amount</label>
                  <input
                    type="text"
                    className="form-control"
                    value={promoCodeForm.discount}
                    onChange={handleInputChange}
                    name="discount"
                  />
                </div>
                <div className="form-group mb-0 col-2">
                  <button className="comman_btn d-flex w-100" type="submit">
                    <span>Add</span>
