import React, { useState, useRef } from 'react';
import { useUserAuth } from '../Components/AuthContext';

const Verification = () => {
  const { verification } = useUserAuth();
  const [otp, setOtp] = useState(['', '', '', '']);

  // Use refs to store references to each input element
  const inputRefs = [useRef(null), useRef(null), useRef(null), useRef(null)];

  const handleInputChange = (index, value) => {
    const newOtp = [...otp];
    newOtp[index] = value;

    setOtp(newOtp);

    // If the current input has a value and it's not the last input, focus on the next input
    if (value && index < 3 && inputRefs[index + 1].current) {
      inputRefs[index + 1].current.focus();
    }
  };

  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && index > 0 && !otp[index]) {
      // Handle backspace and move focus to the previous input
      const newOtp = [...otp];
      newOtp[index - 1] = '';
      setOtp(newOtp);

      if (inputRefs[index - 1].current) {
        inputRefs[index - 1].current.focus();
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Trim whitespaces from the email
    const email = localStorage.getItem('useremail').trim();
    console.log(email);

    // Assuming you have the user's email stored in localStorage
    const joinedOtp = otp.join('');
    verification(email, joinedOtp);
    console.log(joinedOtp);
  };

  return (
    <>
      <section className="login_page">
        <div className="container-fluid px-0">
          <div className="row justify-content-start">
            <div className="col-4">
              <div className="login_page_form shadow">
                <div className="row">
                  <div className="col-12 formheader mb-4">
                    <div className="text-center">
                      <img src="assets/img/logo.png" alt="" />
                    </div>
                    <h1>Verification</h1>
                    <p>Please enter the OTP received on your Email Address </p>
                  </div>
                  <div className="col-12">
                    <form className="row form-design" onSubmit={handleSubmit}>
                      <div className="form-group col-12 otp_input d-flex">
                        {otp.map((digit, index) => (
                          <input
                            key={index}
                            type="text"
                            className="form-control me-3 px-1 text-center"
                            maxLength="1"
                            placeholder={index + 1}
                            name={`digit${index}`}
                            id={`digit${index}`}
                            value={digit}
                            onChange={(e) => handleInputChange(index, e.target.value)}
                            onKeyDown={(e) => handleKeyDown(index, e)}
                            ref={inputRefs[index]}
                          />
                        ))}
                      </div>
                      <div className="form-group col-12 text-center">
                        <span className="count_Sec">00:30</span>
                      </div>
                      <div className="form-group col-12 text-center">
                        <label htmlFor="">Didn't receive the OTP? <a href="javascript:;">Request again</a></label>
                      </div>
                      <div className="form-group col-12">
                        <button type="submit" className="comman_btn">
                          <span>Confirm</span>
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Verification;
