import React from 'react'
import DashboardNavbar from './DashboardNavbar'
import { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
const CategoryView = () => {
    const { _id } = useParams();
    const [data, setData] = useState({});
    const apiBaseUrl = process.env.REACT_APP_API_URL;
    const [imageFileName, setImageFileName] = useState('');
    useEffect(() => {
        const fetchUser = async () => {
            try {
                const authToken = localStorage.getItem('access');

                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const response = await axios.get(`${apiBaseUrl}/api/admin/viewCategories/${_id}`, {
                    headers,
                });
                const userData = response.data.results.categories;
                setData(userData);
            } catch (error) {
                console.error('Error fetching user data:', error);
                throw error;
            }
        };

        fetchUser();
    }, [_id]);
    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
        return new Date(dateString).toLocaleDateString('en-GB', options);
    };

    const handleImageChange = (event) => {
        // Get the name of the selected file
        const fileName = event.target.files[0]?.name;
        setImageFileName(fileName);
    };

    return (
        <>
            <div class="admin_main">

                <DashboardNavbar />

                <div class="admin_panel_data height_adjust">
                    <div class="row mx-0">
                        <div class="col-12 design_outter_comman shadow mb-4 toggle_set">
                            <div class="row comman_header justify-content-between">
                                <div class="col-auto">
                                    <h2>Category Details</h2>
                                </div>
                            </div>
                            <div class="row">
                                <form class="row align-items-center justify-content-center form-design position-relative p-4 py-5">
                                    <div class="col-10">
                                        <div class="row">
                                            <div className="col-12 mb-4">
                                                <div className="row adjust_margin">
                                                    <div className="form-group col-12 mb-2">
                                                        <div className="userinfor_box text-center">
                                                            <span className="user_imgg">
                                                                <img src={data?.image} alt="" />
                                                            </span>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                            <div class="form-group col-6">
                                                <label for="">Category Name</label>
                                                <input disabled type="text" class="form-control" value={data?.name} />
                                            </div>
                                            <div class="form-group col-6">
                                                <label for="">Added Date</label>
                                                <input disabled type="text" value={formatDate(data?.updatedAt)} class="form-control" />
                                            </div>
                                        </div>
                                    </div>
                                    <div class="col-auto"></div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        </>
    )
}

export default CategoryView
