import { createContext, useContext, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const userAuthContext = createContext();

export function UserAuthContextProvider({ children }) {
    const [user, setUser] = useState({});
    const [authtoken, setauthtoken] = useState('')
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const navigate = useNavigate('')
    const login = async (email, password) => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ email, password })
            });

            const data = await response.json();

            if (response.status === 201) {
                setauthtoken(data);
                localStorage.setItem('access', data.access);
                setIsLoggedIn(true);
                navigate('/dashboard');
            } else {
                throw new Error('Email and password do not match');
            }
        } catch (error) {
            throw new Error('An error occurred during login. Please try again later.');
        }
    };
    const forgotPassword = () => {
        // Implement the logic for forgot password if needed
    };

    return (
        <userAuthContext.Provider value={{ login, forgotPassword, user, setauthtoken, authtoken, isLoggedIn }}>
            {children}
        </userAuthContext.Provider>
    );
}

export function useUserAuth() {
    return useContext(userAuthContext);
}
