import { createContext, useContext, useState } from 'react';
// import jwtDecode from 'jwt-decode'; // Make sure to import jwtDecode

const userAuthContext = createContext();

export function UserAuthContextProvider({ children }) {
  const [user, setUser] = useState({});
  const [authtoken, setAuthtoken] = useState(() =>
    localStorage.getItem('token') ? JSON.parse(localStorage.getItem('token')) : null
  );
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const login = async (email, password) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();
      if (response.status === 200) {
        setAuthtoken(data.token);
        // setUser(jwtDecode(data.token)); // Assuming your token contains the necessary user information
        localStorage.setItem('token', data.token);
        setIsLoggedIn(true);
      } else {
        alert('Invalid credentials. Please try again.');
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('Something went wrong. Please try again later.');
    }
  };

  const forgotPassword = () => {
    // Implement the forgotPassword functionality here
  };

  return (
    <userAuthContext.Provider value={{ login, forgotPassword, user, isLoggedIn }}>
      {children}
    </userAuthContext.Provider>
  );
}

export function useUserAuth() {
  return useContext(userAuthContext);
}
