import React, { useState, useEffect } from 'react';
import DashboardNavbar from './DashboardNavbar';
import axios from 'axios';
import { Link } from 'react-router-dom';

const AppointmentManagement = () => {
    const [data, setdata] = useState([]);
    const [listeners, setListeners] = useState([]);
    const [cancelAppointmentId, setCancelAppointmentId] = useState(null);
    const [postponeAppointmentId, setPostponeAppointmentId] = useState(null);
    const apiBaseUrl = process.env.REACT_APP_API_URL;
    useEffect(() => {
        const fetchAppointment = async () => {
            try {
                const authToken = localStorage.getItem('access');
                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/getAllSessions`, {
                    method: 'PATCH',
                    headers: {
                        ...headers,
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }

                const userData = await response.json();
                const sessionsData = userData.results.sessions;
                const userdata = sessionsData.map(session => session.data).flat();
                setdata(userdata);
            } catch (error) {
                console.error('Error fetching appointments:', error);
                // Handle errors accordingly
            }
        };

        const fetchListenerId = async () => {
            try {
                const authToken = localStorage.getItem('access');
                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/listenersList`, {
                    method: 'GET',
                    headers: {
                        ...headers,
                        'Content-Type': 'application/json',
                    },
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! Status: ${response.status}`);
                }

                const listenerData = await response.json();
                const sessionData = listenerData.results.listeners;
                setListeners(sessionData);
            } catch (error) {
                console.error('Error fetching listener IDs:', error);
                // Handle errors accordingly
            }
        };

        const fetchData = async () => {
            await Promise.all([fetchAppointment(), fetchListenerId()]);
        };

        fetchData();
    }, []);

    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
        return new Date(dateString).toLocaleDateString('en-GB', options);
    };

    const getListenerName = (listenerId) => {
        const matchedListener = listeners.find((listener) => listener._id === listenerId);
        return matchedListener ? matchedListener.fullName : 'N/A';
    };
    const cancel_appointment = async (_id) => {
        try {
            const authToken = localStorage.getItem('access');
            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = {
                'x-auth-token-admin': authToken,
            };

            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/cancelAppointment/${_id}`, {
                method: 'PUT',
                headers: {
                    ...headers,
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Handle successful cancellation if needed
            console.log('Appointment canceled successfully!');
            setdata(prevData => {
                return prevData.map(appointment => {
                    if (appointment._id === _id) {
                        return { ...appointment, status: 'Cancelled' };
                    }
                    return appointment;
                });
            });
        } catch (error) {
            console.error('Error canceling appointment:', error);
            // Handle errors accordingly
        } finally {
            // Reset the cancelAppointmentId state
            setCancelAppointmentId(null);
        }
    };

    const postpone_appointment = async (_id) => {
        try {
            const authToken = localStorage.getItem('access');
            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = new Headers({
                'x-auth-token-admin': authToken,
                'Content-Type': 'application/json',
            });

            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/postponeSession/${_id}`, {
                method: 'PUT',
                headers,
                body: JSON.stringify({}), // Empty object as the request body
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const responseData = await response.json();

            if (!responseData.success) {
                throw new Error(`Failed to postpone appointment`);
            }
console.log(_id)
            // Update the state with the appointment ID to be postponed
            setPostponeAppointmentId(_id);
        } catch (error) {
            console.error('Error postponing appointment:', error);
            // Handle errors accordingly
        }
    };

    const handlePostponeSubmit = async (newDateTime) => {
        try {
            if (!postponeAppointmentId) {
                console.error('Appointment ID is null. Unable to make the request.');
                return;
            }

            const authToken = localStorage.getItem('access');
            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = new Headers({
                'x-auth-token-admin': authToken,
                'Content-Type': 'application/json',
            });

            const [date, time] = newDateTime.split('T');
            const formattedTime = time.substring(0, 5);

            const response = await fetch(`${process.env.REACT_APP_API_URL}/api/admin/postponeSession/${postponeAppointmentId}`, {
                method: 'PUT',
                headers,
                body: JSON.stringify({ date, time: formattedTime }),
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const responseData = await response.json();

            if (!responseData.success) {
                throw new Error(`Failed to postpone appointment`);
            }

            // Update the UI with the new data
            const updatedData = data.map((appointment) =>
                appointment._id === postponeAppointmentId
                    ? { ...appointment, date: `${date} ${formattedTime}` }
                    : appointment
            );

            setdata(updatedData);
        } catch (error) {
            console.error('Error postponing appointment:', error);
            // Handle errors accordingly
        } 
    };

    return (
        <>
            <div className="admin_main">
                <DashboardNavbar />
                <div className="admin_panel_data height_adjust">
                    <div className="row mx-0">
                        <div className="col-12 mb-4 px-0">
                            <div className="row justify-content-end">
                                <div className="col-auto">
                                    <a href="javascript:;" className="comman_btn d-inline-flex w-100"><span>Export CSV</span></a>
                                </div>
                            </div>
                        </div>
                        <div className="col-12 design_outter_comman shadow">
                            <div className="row comman_header justify-content-between">
                                <div className="col">
                                    <h2>Appointment Booking Management</h2>
                                </div>
                                <div className="col-3">
                                    <form className="form-design" action="">
                                        <div className="form-group mb-0 position-relative icons_set">
                                            <input type="text" className="form-control" placeholder="Search" name="name" id="name" />
                                            <i className="far fa-search"></i>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">From</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">To</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button className="comman_btn d-flex w-100"><span>Search</span></button>
                                </div>
                            </form>
                            <div className="row">
                                <div className="col-12 comman_table_design px-0">
                                    <div className="table-responsive">
                                        <table className="table mb-0">
                                            <thead>
                                                <tr>
                                                    <th>APPOINTMENT ID</th>
                                                    <th>User Name</th>
                                                    <th>Price</th>
                                                    <th>Listener name</th>
                                                    <th>Date and time</th>
                                                    <th>category of issue</th>
                                                    <th>appointment status</th>
                                                    <th>Appointment Duration</th>
                                                    <th>Mode Of Communication</th>
                                                    <th>Hear Me Out Details</th>
                                                    <th>override appointments</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {data.map((item, index) => (
                                                    <tr key={index}>
                                                        <td>{item._id}</td>
                                                        <td>{item.caller?.fullName}</td>
                                                        <td>{item.amount}</td>
                                                        <td>{getListenerName(item.listener)}</td>
                                                        <td>{formatDate(item.date)}</td>
                                                        <td>{item.reason}</td>
                                                        <td>{item.status}</td>
                                                        <td>{item.time}</td>
                                                        <td>{item.communicationType}</td>
                                                        <td>lorem</td>
                                                        <td>
                                                            {item.status === 'Cancelled' ? (
                                                                <a
                                                                    className="comman_btn bg-danger table_viewbtn"
                                                                >
                                                                    <span>Cancelled</span>
                                                                </a>
                                                            ) : item.status === 'Pending' ? (
                                                                <>
                                                                    <a
                                                                        className="comman_btn table_viewbtn"
                                                                        href="javascript:;"
                                                                        onClick={() => setCancelAppointmentId(item._id)}
                                                                        data-bs-dismiss="modal"
                                                                        data-bs-toggle="modal" data-bs-target="#canceling"                                                                    >
                                                                        <span>Cancel</span>
                                                                    </a>
                                                                    <a
                                                                        className="comman_btn2 table_viewbtn ms-1"
                                                                        href="javascript:;"
                                                                        data-bs-toggle="modal"
                                                                        data-bs-target="#postpone"
                                                                    >
                                                                        <span>Postpone</span>
                                                                    </a>
                                                                </>
                                                            ) : null}
                                                        </td>
                                                        <td>
                                                            <Link className="comman_btn table_viewbtn" to={`/view-appointment/${item._id}`}><span>View</span></Link>
                                                            <a className="comman_btn2 table_viewbtn ms-1" href="javascript:;" data-bs-toggle="modal" data-bs-target="#edit"><span>Edit</span></a>
                                                            <a className="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;"><span>Delete</span></a>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div >

            <div className="modal fade comman_modal edit_modal" id="canceling" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content border-0">
                        <div className="modal-header">
                            <h5 className="modal-title" id="staticBackdropLabel">Appointment Cancel</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body p-0">
                            <form
                                className="form-design py-4 px-4 row"
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    cancel_appointment(cancelAppointmentId);
                                }}
                            >
                                <div className="form-group col-12">
                                    <label htmlFor="">Cancel Reason</label>
                                    <textarea className="form-control" name="reason" id="" style={{ height: '100px' }}></textarea>
                                </div>
                                <div className="form-group mb-0 col-12 text-center">
                                    <button className="comman_btn d-inline-flex" type="submit">
                                        <span>Appointment Cancel</span>
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

            <div className="modal fade comman_modal edit_modal" id="postpone" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content border-0">
                        <div className="modal-header">
                            <h5 className="modal-title" id="staticBackdropLabel">Appointment Postpone</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body p-0">
                            <form
                                className="form-design py-4 px-4 row"
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    const newDateTime = e.target.elements.newDateTime.value;
                                    handlePostponeSubmit(newDateTime);
                                }}
                            >
                                <div className="form-group col-12">
                                    <label htmlFor="">Add New Appointment Date and Time</label>
                                    <input type="datetime-local" className="form-control" name="newDateTime" />
                                </div>
                                <div className="form-group mb-0 col-12 text-center">
                                    <button className="comman_btn d-inline-flex" type="submit">
                                        <span>Appointment Postpone</span>
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

            <div className="modal fade comman_modal" id="edit" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content border-0">
                        <div className="modal-header">
                            <h5 className="modal-title" id="staticBackdropLabel">Edit</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body p-0">
                            <form className="form-design py-4 px-4 row" action="">
                                <div className="form-group col-4">
                                    <label htmlFor="">Username</label>
                                    <input type="text" className="form-control" value="Mohd. Arbab" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Price</label>
                                    <input type="text" className="form-control" value="100 SGD" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Listener Name</label>
                                    <input type="text" className="form-control" value="Jennifer Garcia" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Date and Time</label>
                                    <input type="text" className="form-control" value="Friday, 11 August 2023, 8PM" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Category of Issue</label>
                                    <input type="text" className="form-control" value="Stress" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Appointment Status</label>
                                    <input type="text" className="form-control" value="Completed" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Appointment Duration</label>
                                    <input type="text" className="form-control" value="1 Hour" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Mode Of Communication</label>
                                    <input type="text" className="form-control" value="Video Call" />
                                </div>
                                <div className="form-group col-4">
                                    <label htmlFor="">Hear Me Out Details</label>
                                    <input type="text" className="form-control" value="lorem" />
                                </div>
                                <div className="form-group mb-0 col-12 text-center">
                                    <button className="comman_btn d-inline-flex" data-bs-dismiss="modal"><span>Update</span></button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default AppointmentManagement;
