import React, { useState } from 'react';
import { useUserAuth } from '../Components/AuthContext';

const Verification = () => {
  const { verification } = useUserAuth();
  const [otp, setOtp] = useState(['', '', '', '']);

  
  const handleInputChange = (index, value) => {
    const newOtp = [...otp];
    newOtp[index] = value;

    // If the current input has a value, focus on the next input
    if (value && index < 3) {
      const nextInput = document.getElementById(`digit${index + 1}`);
      if (nextInput) {
        nextInput.focus();
      }
    }

    setOtp(newOtp);
  };

  const handleKeyDown = (index, e) => {
    if (e.key === 'Backspace' && index > 0 && !otp[index]) {
      // Handle backspace and move focus to the previous input
      const newOtp = [...otp];
      newOtp[index - 1] = '';
      setOtp(newOtp);

      const previousInput = document.getElementById(`digit${index - 1}`);
      if (previousInput) {
        previousInput.focus();
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const joinedOtp = otp.join('');
    verification(joinedOtp);
  };

  return (
    <>
      <section className="login_page">
        <div className="container-fluid px-0">
          <div className="row justify-content-start">
            <div className="col-4">
              <div className="login_page_form shadow">
                <div className="row">
                  <div className="col-12 formheader mb-4">
                    <div className="text-center">
                      <img src="assets/img/logo.png" alt="" />
                    </div>
                    <h1>Verification</h1>
                    <p>Please enter the OTP received on your Email Address </p>
                  </div>
                  <div className="col-12">
                    <form className="row form-design" onSubmit={handleSubmit}>
                      <div className="form-group col-12 otp_input d-flex">
                        <input
                          type="text"
                          className="form-control me-3 px-1 text-center"
                          maxLength="1"
                          placeholder="2"
                          name="digit1"
                          id="digit1"
                          value={otp[0]}
                          onChange={(e) => handleInputChange(0, e.target.value)}
                          onKeyDown={(e) => handleKeyDown(0, e)}
                        />
                        <input
                          type="text"
                          className="form-control me-3 px-1 text-center"
                          maxLength="1"
                          placeholder="4"
                          name="digit2"
                          id="digit2"
                          value={otp[1]}
                          onChange={(e) => handleInputChange(1, e.target.value)}
                          onKeyDown={(e) => handleKeyDown(1, e)}
                        />
                        <input
                          type="text"
                          className="form-control me-3 px-1 text-center"
                          maxLength="1"
                          placeholder="6"
                          name="digit3"
                          id="digit3"
                          value={otp[2]}
                          onChange={(e) => handleInputChange(2, e.target.value)}
                          onKeyDown={(e) => handleKeyDown(2, e)}
                        />
                        <input
                          type="text"
                          className="form-control me-3 px-1 text-center"
                          maxLength="1"
                          placeholder="8"
                          name="digit4"
                          id="digit4"
                          value={otp[3]}
                          onChange={(e) => handleInputChange(3, e.target.value)}
                          onKeyDown={(e) => handleKeyDown(3, e)}
                        />
                      </div>
                      <div className="form-group col-12 text-center">
                        <span className="count_Sec">00:30</span>
                      </div>
                      <div className="form-group col-12 text-center">
                        <label htmlFor="">Didn't receive the OTP? <a href="javascript:;">Request again</a></label>
                      </div>
                      <div className="form-group col-12">
                        <button type="submit" className="comman_btn">
                          <span>Confirm</span>
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Verification;
