import React, { useState, useEffect } from 'react';
import DashboardNavbar from './DashboardNavbar';

const PromoCodeManagement = () => {
  const [data, setData] = useState([]);
  const [promoCodeForm, setPromoCodeForm] = useState({
    name: '',
    startDate: '',
    endDate: '',
    discount: '',
  });
  const apiBaseUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    const fetchPromocode = async () => {
      try {
        const authToken = localStorage.getItem('access');
        if (!authToken) {
          throw new Error('Authentication token not found');
        }

        const headers = {
          'x-auth-token-admin': authToken,
        };

        const response = await fetch(`${apiBaseUrl}/api/admin/getAllPromocodes`, {
          method: 'POST',
          headers: {
            ...headers,
            'Content-Type': 'application/json',
          },
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const userData = await response.json();
        setData(userData.results.promocodes);
      } catch (error) {
        console.error('Error fetching promo codes:', error);
        // Handle errors accordingly
      }
    };

    fetchPromocode();
  }, []);

  const formatDate = (dateString) => {
    const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
    return new Date(dateString).toLocaleDateString('en-GB', options);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setPromoCodeForm({
      ...promoCodeForm,
      [name]: value,
    });
  };

  const addPromocode = async (e) => {
    e.preventDefault();

    try {
      const authToken = localStorage.getItem('access');
      if (!authToken) {
        throw new Error('Authentication token not found');
      }

      const headers = {
        'x-auth-token-admin': authToken,
        'Content-Type': 'application/json',
      };

      const response = await fetch(`${apiBaseUrl}/api/admin/addPromocode`, {
        method: 'POST',
        headers,
        body: JSON.stringify(promoCodeForm),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const result = await response.json();
      console.log(result);

      // Optionally, update the data state with the new promo code
      setData([...data, result.results.promocode]);

      // Clear the form fields
      setPromoCodeForm({
        name: '',
        startDate: '',
        endDate: '',
        discount: '',
      });
    } catch (error) {
      console.error('Error adding promo code:', error);
      // Handle errors accordingly
    }
  };

  return (
    <>
      <div className='admin_main'>
        <DashboardNavbar />
        <div className="admin_panel_data height_adjust">
          <div className="row mx-0">
            <div className="col-12 design_outter_comman shadow mb-4">
              <div className="row comman_header justify-content-between">
                <div className="col">
                  <h2>Add Promo Code</h2>
                </div>
              </div>
              <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" onSubmit={addPromocode}>
                <div className="form-group col-4">
                  <label htmlFor="promoCode">Promo Code</label>
                  <input
                    type="text"
                    className="form-control"
                    name="name"
                    value={promoCodeForm.name}
                    onChange={handleInputChange}
                  />
                </div>
                {/* ... (rest of your form fields) */}
                <div className="form-group mb-0 col-2">
                  <button className="comman_btn d-flex w-100" type="submit">
                    <span>Add</span>
                  </button>
                </div>
              </form>
            </div>
            {/* ... (rest of your JSX code) */}
          </div>
        </div>
      </div>
    </>
  );
};

export default PromoCodeManagement;
