import React, { createContext, useState } from "react";
import axios from "axios";
import { Navigate, useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
const UserContext = createContext();

export default UserContext;

export const AuthProvider = ({ children }) => {
    const [authtoken, setauthtoken] = useState(() => localStorage.getItem('authtoken') ? JSON.parse(localStorage.getItem('authtoken')) : null)
    const [user, setuser] = useState(() => localStorage.getItem('authtoken') ? jwtDecode(localStorage.getItem('authtoken')) : null)
    const [isActivated, setIsActivated] = useState(false);
    const [error, setError] = useState(null);
    const navigate = useNavigate()
    const logout = () => {
        setauthtoken(null)
        setuser(null)
        localStorage.removeItem('access')
        navigate('/login')
    }
    const signup = async (e) => {
        e.preventDefault()

        const response = await fetch('http://localhost:8000/auth/users/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ 'name': e.target.name.value, 'email': e.target.email.value, 'password': e.target.password.value, 're_password': e.target.re_password.value })
        })
        console.log('Ok')
        const data = await response.json()
        console.log(data)
    }

    const login = async (e) => {
        e.preventDefault();
        const response = await fetch('http://localhost:8000/api/token', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ 'email': e.target.email.value, 'password': e.target.password.value })
        })
        const data = await response.json();
        if (response.status == 200) {
            setauthtoken(data)
            setuser(jwtDecode(data.access))
            // console.log(jwtDecode(data.access))
            localStorage.setItem('access', data.access);
            navigate('/dashboard')
        } else {
            alert('Something Went Wrong');
        }
    }
    const reset_password = async (email) => {
        const apiUrl = process.env.REACT_APP_API_URL;
        console.log('API URL:', apiUrl);

        const config = {
            headers: {
                'Content-Type': 'application/json',
            },
        };

        const body = JSON.stringify({ email });
        console.log('Request Body:', body);

        try {
            const response = await axios.post('http://localhost:8000/auth/users/reset_password/', body, config);
            console.log('Response:', response);
            if (response.status === 204) {
                console.log('Password reset request sent.');
                navigate('/login');
            } else {
                console.error('Password reset request failed. Server returned status:', response.status);
            }
        } catch (error) {
            console.error('An error occurred during password reset:', error);
        }

    }

    const reset_password_confirm = async (uid, token, new_password) => {
        const config = {
            headers: {
                'Content-Type': 'application/json',
            }
        };

        const body = JSON.stringify({ uid, token, new_password });
        console.log('Request Payload:', body);

        try {
            const response = await axios.post('http://localhost:8000/auth/users/reset_password_confirm/', body, config);
            console.log('Password reset confirmed successfully:', response.data);
            if (response) {
                Navigate('/login')
            }
        } catch (error) {
            console.log(error)
        }
    };
    const verify = async (uid, token) => {
        const config = {
            headers: {
                'Content-Type': 'application/json',
            },
        };

        const body = JSON.stringify({ uid, token });

        try {
            await axios.post('http://localhost:8000/auth/users/activation/', body, config);
            setIsActivated(true);
            setError(null);
        } catch (err) {
            setIsActivated(false);
            setError(err);
        }
    };
    const contextData = {
        signup: signup,
        isActivated,
        error,
        reset_password: reset_password,
        login: login,
        user: user,
        logout: logout,
        reset_password_confirm: reset_password_confirm,
        verify:verify,
    }
    return (
        <UserContext.Provider value={contextData}>
            {children}
        </UserContext.Provider>
    )
}
