import React from 'react'
import DashboardNavbar from './DashboardNavbar'
import { useState, useEffect } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
const CategoryManagement = () => {
    const [data, setdata] = useState([])
    const [categoryName, setCategoryName] = useState('');
    const [categoryImage, setCategoryImage] = useState(null);
    const apiBaseUrl = process.env.REACT_APP_API_URL;
    useEffect(() => {
        const fetchUser = async () => {
            try {
                // Retrieve the authentication token from local storage
                const authToken = localStorage.getItem('access');

                // Check if the token exists
                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                // Set up headers with the authentication token
                const headers = {
                    'x-auth-token-admin': authToken,
                };

                // Make the HTTP request with headers
                const response = await axios.get(`${apiBaseUrl}/api/admin/getCategories`, {
                    headers,
                });

                // Assuming the API response contains user data, you can access it like this:
                const userData = response.data.results.categories;
                setdata(userData);
            } catch (error) {
                // Handle error scenarios here
                console.error('Error fetching user data:', error);
                throw error; // You may want to throw the error or handle it in a specific way.
            }
        };
        fetchUser();
    }, []);
    const handleNameChange = (event) => {
        setCategoryName(event.target.value);
    };

    const handleImageChange = (event) => {
        setCategoryImage(event.target.files[0]);
    };
    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
        return new Date(dateString).toLocaleDateString('en-GB', options);
    };
    const addCategory = async () => {
        const formData = new FormData();
        formData.append('name', categoryName);
        formData.append('image', categoryImage);

        try {
            const authToken = localStorage.getItem('access');
            const headers = {
                'x-auth-token-admin': authToken,
            };

            const response = await fetch('http://ec2-52-66-186-107.ap-south-1.compute.amazonaws.com:3009/api/admin/newCategory', {
                method: 'POST',
                body: formData,
                headers: headers,
            });

            const responseData = await response.json();

            if (!responseData.error) {
                // Success
                console.log(responseData.message);
                console.log(responseData.results.newCat);
            } else {
                // Handle errors
                console.error(responseData.message);
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        addCategory();
    };
    const userList = Array.isArray(data) ? data : [];
    return (

        <>
            <div class="admin_main">

                <DashboardNavbar />

                <div class="admin_panel_data height_adjust">
                    <div class="row mx-0">
                        <div class="col-12 design_outter_comman shadow mb-4">
                            <div class="row comman_header justify-content-between">
                                <div class="col">
                                    <h2>Add New Category </h2>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" onSubmit={handleSubmit}>
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="categoryName">Category Name</label>
                                    <input type="text" name="name" id="categoryName" className="form-control" value={categoryName} onChange={handleNameChange} />
                                </div>
                                <div className="form-group mb-0 col-5 choose_file position-relative">
                                    <span>Category Image</span>
                                    <label htmlFor="upload_video"><i className="fal fa-camera me-1"></i>Choose File</label>
                                    <input type="file" className="form-control" name="image" id="upload_video" onChange={handleImageChange} />
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button type="submit" className="comman_btn d-flex w-100"><span>Add</span></button>
                                </div>
                            </form>
                        </div>
                        <div class="col-12 design_outter_comman shadow">
                            <div class="row comman_header justify-content-between">
                                <div class="col">
                                    <h2>Category Management</h2>
                                </div>
                                <div class="col-3">
                                    <form class="form-design" action="">
                                        <div class="form-group mb-0 position-relative icons_set">
                                            <input type="text" class="form-control" placeholder="Search" name="name" id="name" />
                                            <i class="far fa-search"></i>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <form class="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                                <div class="form-group mb-0 col-5">
                                    <label for="">From</label>
                                    <input type="date" class="form-control" />
                                </div>
                                <div class="form-group mb-0 col-5">
                                    <label for="">To</label>
                                    <input type="date" class="form-control" />
                                </div>
                                <div class="form-group mb-0 col-2">
                                    <button class="comman_btn d-flex w-100"><span>Search</span></button>
                                </div>
                            </form>
                            <div class="row">
                                <div class="col-12 comman_table_design px-0">
                                    <div class="table-responsive">
                                        <table class="table mb-0">
                                            <thead>
                                                <tr>
                                                    <th>S.No.</th>
                                                    <th>Category image</th>
                                                    <th>Category Name</th>
                                                    <th>Added Date</th>
                                                    <th>Status</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {data.map((item, index) => (
                                                    <tr key={index}>
                                                        <td>{index + 1}</td>
                                                        <td><img class="table_img" src={item.image} alt="" /></td>
                                                        <td>{item.name}  </td>

                                                        <td>{formatDate(item.updatedAt)}</td>
                                                        <td>
                                                            <div class="check_toggle">{item.status}
                                                                <input type="checkbox" name="maintance" id="maintance" class="d-none" />
                                                                <label for="maintance"></label>
                                                            </div>
                                                        </td>
                                                        <td>
                                                            <Link to={`/category-view/${item._id}`} class="comman_btn table_viewbtn" href="category-view.html"><span>View</span></Link>
                                                            <a class="comman_btn2 table_viewbtn ms-1" href="javascript:;" data-bs-toggle="modal" data-bs-target="#edit"><span>Edit</span></a>
                                                            <a class="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;"><span>Delete</span></a>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default CategoryManagement
