import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DashboardNavbar from './DashboardNavbar';

const AppointmentManagement = () => {
    const [data, setData] = useState([]);
    const [listeners, setListeners] = useState([]);
    const [cancelAppointmentId, setCancelAppointmentId] = useState(null);
    const [postponeAppointmentId, setPostponeAppointmentId] = useState(null);
    const apiBaseUrl = process.env.REACT_APP_API_URL;

    useEffect(() => {
        const fetchData = async () => {
            try {
                const authToken = localStorage.getItem('access');
                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const [appointmentsResponse, listenersResponse] = await Promise.all([
                    axios.get(`${apiBaseUrl}/api/admin/getAllSessions`, { headers }),
                    axios.get(`${apiBaseUrl}/api/admin/listenersList`, { headers }),
                ]);

                const userData = appointmentsResponse.data.results.sessions;
                const appointmentsData = userData.map((session) => session.data).flat();
                setData(appointmentsData);

                const listenerData = listenersResponse.data.results.listeners;
                setListeners(listenerData);
            } catch (error) {
                console.error('Error fetching data:', error);
                // Handle errors accordingly
            }
        };

        fetchData();
    }, []);

    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
        return new Date(dateString).toLocaleDateString('en-GB', options);
    };

    const getListenerName = (listenerId) => {
        const matchedListener = listeners.find((listener) => listener._id === listenerId);
        return matchedListener ? matchedListener.fullName : 'N/A';
    };

    const cancelAppointment = async (_id) => {
        try {
            const authToken = localStorage.getItem('access');
            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = {
                'x-auth-token-admin': authToken,
            };

            const response = await axios.put(`${apiBaseUrl}/api/admin/cancelAppointment/${_id}`, null, {
                headers,
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Handle successful cancellation if needed
            console.log('Appointment canceled successfully!');
            setData((prevData) =>
                prevData.map((appointment) =>
                    appointment._id === _id ? { ...appointment, status: 'Cancelled' } : appointment
                )
            );
        } catch (error) {
            console.error('Error canceling appointment:', error);
            // Handle errors accordingly
        } finally {
            // Reset the cancelAppointmentId state
            setCancelAppointmentId(null);
        }
    };

    const postponeAppointment = async (_id, newDate, newTime) => {
        try {
            const authToken = localStorage.getItem('access');
            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = {
                'x-auth-token-admin': authToken,
                'Content-Type': 'application/json',
            };

            const response = await axios.put(
                `${apiBaseUrl}/api/admin/postponeSession/${_id}`,
                { newDate, newTime },
                { headers }
            );

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            // Handle successful postponement if needed
            console.log('Appointment Postponed successfully!');
        } catch (error) {
            console.error('Error postponing appointment:', error);
            // Handle errors accordingly
        } finally {
            // Reset the postponeAppointmentId state
            setPostponeAppointmentId(null);
        }
    };
    return (
        <>
            <div className="admin_main">
                <DashboardNavbar />
                <div className="admin_panel_data height_adjust">
                    <div className="row mx-0">
                        <div className="col-12 mb-4 px-0">
                            <div className="row justify-content-end">
                                <div className="col-auto">
                                    <a href="javascript:;" className="comman_btn d-inline-flex w-100"><span>Export CSV</span></a>
                                </div>
                            </div>
                        </div>
                        <div className="col-12 design_outter_comman shadow">
                            <div className="row comman_header justify-content-between">
                                <div className="col">
                                    <h2>Appointment Booking Management</h2>
                                </div>
                                <div className="col-3">
                                    <form className="form-design" action="">
                                        <div className="form-group mb-0 position-relative icons_set">
                                            <input type="text" className="form-control" placeholder="Search" name="name" id="name" />
                                            <i className="far fa-search"></i>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">From</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">To</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button className="comman_btn d-flex w-100"><span>Search</span></button>
                                </div>
                            </form>
                            <div className="row">
                                <div className="col-12 comman_table_design px-0">
                                    <div className="table-responsive">
                                        <table className="table mb-0">
                                            <thead>
                                                <tr>
                                                    <th>APPOINTMENT ID</th>
                                                    <th>User Name</th>
                                                    <th>Price</th>
                                                    <th>Listener name</th>
                                                    <th>Date and time</th>
                                                    <th>category of issue</th>
                                                    <th>appointment status</th>
                                                    <th>Appointment Duration</th>
                                                    <th>Mode Of Communication</th>
                                                    <th>Hear Me Out Details</th>
                                                    <th>override appointments</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {data.map((item, index) => (
                                                    <tr key={index}>
                                                        <td>{item._id}</td>
                                                        <td>{item.caller?.fullName}</td>
                                                        <td>{item.amount}</td>
                                                        <td>{getListenerName(item.listener)}</td>
                                                        <td>{formatDate(item.date)}</td>
                                                        <td>{item.reason}</td>
                                                        <td>{item.status}</td>
                                                        <td>{item.time}</td>
                                                        <td>{item.communicationType}</td>
                                                        <td>lorem</td>
                                                        <td>
                                                            {item.status === 'Cancelled' ? (
                                                                <a
                                                                    className="comman_btn bg-danger table_viewbtn"
                                                                >
                                                                    <span>Cancelled</span>
                                                                </a>
                                                            ) : item.status === 'Pending' ? (
                                                                <>
                                                                    <a
                                                                        className="comman_btn table_viewbtn"
                                                                        href="javascript:;"
                                                                        onClick={() => setCancelAppointmentId(item._id)}
                                                                        data-bs-dismiss="modal"
                                                                        data-bs-toggle="modal" data-bs-target="#canceling"                                                                    >
                                                                        <span>Cancel</span>
                                                                    </a>
                                                                    <a
                                                                        className="comman_btn2 table_viewbtn ms-1"
                                                                        href="javascript:;"
                                                                        data-bs-toggle="modal"
                                                                        data-bs-target="#postpone"
                                                                        onClick={() => setPostponeAppointmentId(item._id)}

                                                                    >
                                                                        <span>Postpone</span>
                                                                    </a>
                                                                </>
                                                            ) : null}
                                                        </td>
                                                        <td>
                                                            <Link className="comman_btn table_viewbtn" to={`/view-appointment/${item._id}`}><span>View</span></Link>
                                                            <a className="comman_btn2 table_viewbtn ms-1" href="javascript:;" data-bs-toggle="modal" data-bs-target="#edit"><span>Edit</span></a>
                                                            <a className="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;"><span>Delete</span></a>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div >

            <div className="modal fade comman_modal edit_modal" id="canceling" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content border-0">
                        <div className="modal-header">
                            <h5 className="modal-title" id="staticBackdropLabel">Appointment Cancel</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body p-0">
                            <form
                                className="form-design py-4 px-4 row"
                                onSubmit={(e) => {
                                    e.preventDefault();
                                    cancel_appointment(cancelAppointmentId);
                                }}
                            >
                                <div className="form-group col-12">
                                    <label htmlFor="">Cancel Reason</label>
                                    <textarea className="form-control" name="reason" id="" style={{ height: '100px' }}></textarea>
                                </div>
                                <div className="form-group mb-0 col-12 text-center">
                                    <button className="comman_btn d-inline-flex" type="submit">
                                        <span>Appointment Cancel</span>
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

            <div className="modal fade comman_modal edit_modal" id="postpone" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog modal-dialog-centered">
                    <div className="modal-content border-0">
                        <div className="modal-header">
                            <h5 className="modal-title" id="staticBackdropLabel">Appointment Postpone</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body p-0">
                            <form className="form-design py-4 px-4 row" onSubmit={(e) => {
                                e.preventDefault();
                                const newDate = e.target.elements.date.value;
                                const newTime = e.target.elements.time.value;
                                postpone_appointment(postponeAppointmentId, newDate, newTime);
                            }}>
