import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const ListenerManagement = () => {
  const [data, setData] = useState([]);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    profileDescription: '',
    experiences: '',
    commission: '',
    serviceLanguage: '',
    id: null,
  });

  const apiBaseUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const authToken = localStorage.getItem('access');

        if (!authToken) {
          throw new Error('Authentication token not found');
        }

        const headers = {
          'x-auth-token-admin': authToken,
        };

        const response = await fetch(`${apiBaseUrl}/api/admin/getAllListeners`, {
          headers,
        });

        const responseData = await response.json();

        if (!responseData.error) {
          const userData = responseData.results.listeners;
          setData(userData);
        } else {
          throw new Error(responseData.message);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        // Handle error scenarios here
      }
    };

    fetchUser();
  }, []);

  const listenerStatus = async (id) => {
    console.log(id);
    try {
      const authToken = localStorage.getItem('access');
      const headers = {
        'x-auth-token-admin': authToken,
      };

      const response = await fetch(`${apiBaseUrl}/api/admin/changeListenerStatus/${id}`, {
        method: 'GET',
        headers: headers,
      });

      const responseData = await response.json();

      if (!responseData.error) {
        setData((prevData) =>
          prevData.map((item) =>
            item._id === id ? { ...item, status: !item.status } : item
          )
        );

        localStorage.setItem('listener_stats', JSON.stringify({ ...cat, [id]: !cat[id] }));
      } else {
        alert(responseData.message);
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  const updateFormData = (name, value) => {
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const update = async () => {
    try {
      const authToken = localStorage.getItem('access');
      const headers = new Headers({
        'x-auth-token-admin': authToken,
        'Content-Type': 'application/json',
      });

      const response = await fetch(`${apiBaseUrl}/api/admin/editListener`, {
        method: 'PUT',
        headers,
        body: JSON.stringify(formData),
      });

      const responseData = await response.json();

      if (!responseData.error) {
        // Reset the form data
        setFormData({
          firstName: '',
          lastName: '',
          email: '',
          profileDescription: '',
          experiences: '',
          commission: '',
          serviceLanguage: '',
          id: null,
        });

        // Close the modal after successful update
        document.getElementById('edit').click();
      } else {
        alert(responseData.message);
      }
    } catch (error) {
      console.error('Error updating listener: ', error);
    }
  };


    return (
        <>
            <div class="admin_main">
                <DashboardNavbar />
                <div class="admin_panel_data height_adjust">
                    <div class="row mx-0">
                        <div class="col-12 mb-4 px-0">
                            <div class="row justify-content-end">
                                <div class="col-auto">
                                    <a href="javascript:;" class="comman_btn d-inline-flex w-100"><span>Export CSV</span></a>
                                </div>
                            </div>
                        </div>
                        <div class="col-12 design_outter_comman shadow mb-4">
                            <div class="row comman_header justify-content-between">
                                <div class="col">
                                    <h2>Add Commission</h2>
                                </div>
                            </div>
                            <form class="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                                <div class="form-group mb-0 col">
                                    <label for="">Add Commission</label>
                                    <input type="text" class="form-control" />
                                </div>
                                <div class="form-group mb-0 col-2">
                                    <button class="comman_btn d-flex w-100"><span>Add</span></button>
                                </div>
                            </form>
                        </div>
                        <div class="col-12 design_outter_comman shadow">
                            <div class="row comman_header justify-content-between">
                                <div class="col">
                                    <h2>Listener Management</h2>
                                </div>
                                <div class="col-3">
                                    <form class="form-design" action="">
                                        <div class="form-group mb-0 position-relative icons_set">
                                            <input type="text" class="form-control" placeholder="Search" name="name" id="name" />
                                            <i class="far fa-search"></i>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <form class="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                                <div class="form-group mb-0 col-5">
                                    <label for="">From</label>
                                    <input type="date" class="form-control" />
                                </div>
                                <div class="form-group mb-0 col-5">
                                    <label for="">To</label>
                                    <input type="date" class="form-control" />
                                </div>
                                <div class="form-group mb-0 col-2">
                                    <button class="comman_btn d-flex w-100"><span>Search</span></button>
                                </div>
                            </form>
                            <div class="row">
                                <div class="col-12 comman_table_design px-0">
                                    <div class="table-responsive">
                                        <table class="table mb-0">
                                            <thead>
                                                <tr>
                                                    <th>S.No.</th>
                                                    <th>Listener Name</th>
                                                    <th>Email ID</th>
                                                    <th>mobile number</th>
                                                    <th>listener details</th>
                                                    <th>experience</th>
                                                    <th>Commission</th>
                                                    <th>language proficiency</th>
                                                    <th>Booked appointment details</th>
                                                    <th>Classify listener</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {data.map((item, index) => (
                                                    <tr key={index}>
                                                        <td>{index + 1}</td>
                                                        <td>{item.firstName}{item.lastName}</td>
                                                        <td>{item.email}</td>
                                                        <td>{item.phoneNumber}</td>
                                                        <td>
                                                            {item.profileDescription}
                                                        </td>
                                                        <td>{item.experience} yrs</td>
                                                        <td>10%</td>
                                                        <td>{String(item.serviceLanguage).replace(/([a-z])([A-Z])/g, '$1 $2')}</td>
                                                        <td>
                                                            Friday, 11 August 2023, 8PM <br />
                                                            Video Call
                                                        </td>

                                                        <td>
                                                            Listener+
                                                        </td>
                                                        <td>
                                                            <Link to={`/listener-view/${item._id}`} class="comman_btn table_viewbtn"><span>View</span></Link>
                                                            <a class="comman_btn2 table_viewbtn ms-1" href="javascript:;" data-bs-toggle="modal" data-bs-target="#edit"><span>Edit</span></a>
                                                            <a
                                                                onClick={() => listener_status(item._id)}
                                                                className={`comman_btn ${item.status ? 'black block' : 'bg-success unblock'} table_viewbtn ms-1`}

                                                                href="javascript:;"
                                                            >
                                                                <span>{item.status ? 'Block' : 'Unblock'}</span>
                                                            </a>
                                                            <a class="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;"><span>Delete</span></a>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div class="modal fade comman_modal" id="edit" data-bs-backdrop="static" data-bs-keyboard="false" tabindex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div class="modal-dialog modal-dialog-centered">
                    <div class="modal-content border-0">
                        <div class="modal-header">
                            <h5 class="modal-title" id="staticBackdropLabel">
                                Edit
                            </h5>
                            <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div class="modal-body p-0">
                            <form class="form-design py-4 px-4 row" action="">
                                <div class="form-group col-4">
                                    <label for="">First Name</label>
                                    <input
                                        type="text"
                                        class="form-control"
                                        name="firstName"
                                        value={formData.firstName}
                                        onChange={(e) => updateFormData('firstName', e.target.value)}
                                    />
                                </div>
                                <div class="form-group col-4">
                                    <label for="">Last Name</label>
                                    <input
                                        type="text"
                                        class="form-control"
                                        name="lastName"
                                        value={formData.lastName}
                                        onChange={(e) => updateFormData('lastName', e.target.value)}
                                    />
                                </div>
                                <div class="form-group col-4">
                                    <label for="">Email ID</label>
                                    <input
                                        type="text"
                                        class="form-control"
                                        name="email"
                                        value={formData.email}
                                        onChange={(e) => updateFormData('email', e.target.value)}
                                    />
                                </div>
                                <div class="form-group col-12">
                                    <label for="">Listener Details</label>
                                    <textarea
                                        class="form-control"
                                        name="profileDescription"
                                        value={formData.profileDescription}
                                        onChange={(e) => updateFormData('profileDescription', e.target.value)}
                                        style={{ height: '60px' }}
                                    ></textarea>
                                </div>
                                <div class="form-group col-4">
                                    <label for="">Experience</label>
                                    <input
                                        type="text"
                                        name="experiences"
                                        value={formData.experiences}
                                        onChange={(e) => updateFormData('experiences', e.target.value)}
                                        class="form-control"
                                    />
                                </div>
                                <div class="form-group col-4">
                                    <label for="">Commission</label>
                                    <input
                                        type="text"
                                        value={formData.commission}
                                        onChange={(e) => updateFormData('commission', e.target.value)}
                                        class="form-control"
                                    />
                                </div>
                                <div class="form-group col-4">
                                    <label for="">Language Proficiency</label>
                                    <input
                                        type="text"
                                        name="serviceLanguage"
                                        value={formData.serviceLanguage}
                                        onChange={(e) => updateFormData('serviceLanguage', e.target.value)}
                                        class="form-control"
                                    />
                                </div>
                                <div class="form-group mb-0 col-12 text-center">
                                    <button class="comman_btn d-inline-flex" data-bs-dismiss="modal" onClick={update}>
                                        <span>Update</span>
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}

export default ListenerMangement
