import React, { useEffect, useState } from 'react';
import DashboardNavbar from './DashboardNavbar';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const CategoryView = () => {
    const { _id } = useParams();
    const [data, setData] = useState({});
    const [categoryName, setCategoryName] = useState('');
    const [categoryImage, setCategoryImage] = useState(null);
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(null);
    const apiBaseUrl = process.env.REACT_APP_API_URL;

    useEffect(() => {
        const fetchCategory = async () => {
            try {
                const authToken = localStorage.getItem('access');

                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const response = await axios.get(`${apiBaseUrl}/api/admin/editCategory/${_id}`, {
                    headers,
                });
                const userData = response.data.results.categories;
                setData(userData);
                setCategoryName(userData.name);
            } catch (error) {
                console.error('Error fetching category data:', error);
                throw error;
            }
        };

        fetchCategory();
    }, [_id]);

    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
        return new Date(dateString).toLocaleDateString('en-GB', options);
    };

    const handleFileChange = (event) => {
        // Handle file change if needed
        // setCategoryImage(event.target.files[0]);
    };

    const handleCategoryNameChange = (event) => {
        setCategoryName(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const authToken = localStorage.getItem('access');

            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = {
                'x-auth-token-admin': authToken,
                'Content-Type': 'application/json',
            };

            const requestBody = {
                name: categoryName,
                // Add other fields as needed
            };

            const response = await axios.put(`${apiBaseUrl}/api/admin/updateCategory/${_id}`, requestBody, {
                headers,
            });

            // Handle success
            setSuccess(true);
        } catch (error) {
            console.error('Error updating category:', error);
            setError(error.message || 'An error occurred while updating the category');
        }
    };

    return (
        <>
            {/* Add success and error message components as needed */}

            <div className="admin_main">
                <DashboardNavbar />
                <div className="admin_panel_data height_adjust">
                    <div className="row mx-0">
                        <div className="col-12 design_outter_comman shadow mb-4 toggle_set">
                            <div className="row comman_header justify-content-between">
                                <div className="col-auto">
                                    <h2>Update Category Details</h2>
                                </div>
                            </div>
                            <div className="row">
                                <form className="row align-items-center justify-content-center form-design position-relative p-4 py-5" onSubmit={handleSubmit}>
                                    <div className="col-10">
                                        <div className="row">
                                            <div className="form-group col-12 choose_file position-relative">
                                                <span>Category Image</span>
                                                <label htmlFor="upload_video">
                                                    <i className="fal fa-camera me-1"></i>Choose File
                                                </label>
                                                <input type="file" className="form-control" onChange={handleFileChange} name="upload_video" id="upload_video" />
                                            </div>
                                            <div className="form-group col-6">
                                                <label htmlFor="">Category Name</label>
                                                <input type="text" className="form-control" value={categoryName} onChange={handleCategoryNameChange} />
                                            </div>
                                            <div className="form-group col-6">
                                                <label htmlFor="">Added Date</label>
                                                <input type="text" value={formatDate(data?.updatedAt)} className="form-control" readOnly />
                                            </div>
                                            <div className="form-group mb-0 col-12 text-center">
                                                <button type="submit" className="comman_btn d-inline-flex">
                                                    <span>Update</span>
                                                </button>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="col-auto"></div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default CategoryView;
