import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DashboardNavbar from './DashboardNavbar';

const CategoryManagement = () => {
    const [data, setdata] = useState([]);
    const [categoryName, setCategoryName] = useState('');
    const [categoryImage, setCategoryImage] = useState(null);

    const apiBaseUrl = process.env.REACT_APP_API_URL;

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const authToken = localStorage.getItem('access');

                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const response = await axios.get(`${apiBaseUrl}/api/admin/getCategories`, {
                    headers,
                });

                const categoriesData = response.data.results.categories;

                console.log(categoriesData);
                setdata(categoriesData);
            } catch (error) {
                console.error('Error fetching category data:', error);
                throw error;
            }
        };

        fetchCategories();
    }, []);

    const addCategory = async (e) => {
        e.preventDefault()
        try {
            const authToken = localStorage.getItem('access');
            if (!categoryName || !categoryImage) {
                alert('')
                return;
            }
            if (!authToken) {
                throw new Error('Authentication token not found');
            }

            const headers = {
                'x-auth-token-admin': authToken,
                'Content-Type': 'multipart/form-data',
            };

            const formData = new FormData();
            formData.append('categoryName', categoryName);
            formData.append('categoryImage', categoryImage);

            const response = await axios.post(`${apiBaseUrl}/api/admin/newCategory`, formData, {
                headers,
            });

            console.log(formData);
        } catch (error) {
            console.error('Error adding new category:', error);
            throw error;
        }
    };

    const handleFileChange = (e) => {
        setCategoryImage(e.target.files[0]);
    };

    const categoryList = Array.isArray(data) ? data : [];

    return (
        <>
            <div className="admin_main">
                <DashboardNavbar />

                <div className="admin_panel_data height_adjust">
                    <div className="row mx-0">
                        {/* Add New Category */}
                        <div className="col-12 design_outter_comman shadow mb-4">
                            <div className="row comman_header justify-content-between">
                                <div className="col">
                                    <h2>Add New Category</h2>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between">
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="categoryName">Category Name</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        id="categoryName"
                                        value={categoryName}
                                        onChange={(e) => setCategoryName(e.target.value)}
                                    />
                                </div>
                                <div className="form-group mb-0 col-5 choose_file position-relative">
                                    <span>Category Image</span>
                                    <label htmlFor="upload_category_image" className="cursor-pointer">
                                        <i className="fal fa-camera me-1"></i>Choose File
                                    </label>
                                    <input
                                        type="file"
                                        className="form-control d-none"
                                        name="upload_category_image"
                                        id="upload_category_image"
                                        onChange={handleFileChange}
                                    />
                                    {/* Display the selected file name */}
                                    {categoryImage && <p>{categoryImage.name}</p>}
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button className="comman_btn d-flex w-100" onClick={addCategory}>
                                        <span>Add</span>
                                    </button>
                                </div>
                            </form>
                        </div>

                        {/* Category Management Table */}
                        <div className="col-12 design_outter_comman shadow">
                            <div className="row comman_header justify-content-between">
                                <div className="col">
                                    <h2>Category Management</h2>
                                </div>
                                <div className="col-3">
                                    <form className="form-design" action="">
                                        <div className="form-group mb-0 position-relative icons_set">
                                            <input type="text" className="form-control" placeholder="Search" name="name" id="name" />
                                            <i className="far fa-search"></i>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between">
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">From</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">To</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button className="comman_btn d-flex w-100">
                                        <span>Search</span>
                                    </button>
                                </div>
                            </form>
                            <div className="row">
                                <div className="col-12 comman_table_design px-0">
                                    <div className="table-responsive">
                                        <table className="table mb-0">
                                            <thead>
                                                <tr>
                                                    <th>S.No.</th>
                                                    <th>Category Image</th>
                                                    <th>Category Name</th>
                                                    <th>Added Date</th>
                                                    <th>Status</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {categoryList.map((item, index) => (
                                                    <tr key={index}>
                                                        <td>{index + 1}</td>
                                                        <td>
                                                            <img className="table_img" src={item.image} alt="" />
                                                        </td>
                                                        <td>{item.name}</td>
                                                        <td>19/10/23</td>
                                                        <td>
                                                            <div className="check_toggle">
                                                                {item.status}
                                                                <input type="checkbox" name="maintenance" id="maintenance" className="d-none" />
                                                                <label htmlFor="maintenance"></label>
                                                            </div>
                                                        </td>
                                                        <td>
                                                            <a className="comman_btn table_viewbtn" href="category-view.html">
                                                                <span>View</span>
                                                            </a>
                                                            <a className="comman_btn2 table_viewbtn ms-1" href="javascript:;" data-bs-toggle="modal" data-bs-target="#edit">
                                                                <span>Edit</span>
                                                            </a>
                                                            <a className="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;">
                                                                <span>Delete</span>
                                                            </a>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default CategoryManagement;
