import React, { useState } from 'react';

const ForgotPassword = () => {
    const [email, setEmail] = useState('');
    const [submitted, setSubmitted] = useState(false);

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validation logic
        if (!email) {
            // If email is empty, set submitted to true to show the error message
            setSubmitted(true);
        } else {
            // Handle the form submission logic here (e.g., send a request to the server)
            // For now, you can just log the email
            console.log('Email submitted:', email);
        }
    };

    return (
        <>
            <section className="login_page">
                <div className="container-fluid px-0">
                    <div className="row justify-content-start">
                        <div className="col-4">
                            <div className="login_page_form shadow">
                                <div className="row">
                                    <div className="col-12 formheader mb-4">
                                        <div className="text-center">
                                            <img src="assets/img/logo.png" alt="" />
                                        </div>
                                        <h1>Forgot Password</h1>
                                        {submitted && !email && <p className="error-message">Please enter your email address.</p>}
                                    </div>
                                    <div className="col-12">
                                        <form className="row form-design" onSubmit={handleSubmit}>
                                            <div className="form-group col-12">
                                                <label htmlFor="name">Email Address</label>
                                                <input
                                                    type="text"
                                                    className="form-control"
                                                    placeholder="User@gmail.com"
                                                    name="name"
                                                    id="name"
                                                    value={email}
                                                    onChange={(e) => setEmail(e.target.value)}
                                                />
                                            </div>
                                            <div className="form-group col-12">
                                                <button type="submit" className="comman_btn">
                                                    <span>Submit</span>
                                                </button>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>
        </>
    );
};

export default ForgotPassword;
