import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import DashboardNavbar from './DashboardNavbar';

const CategoryManagement = () => {
  const [data, setData] = useState([]);
  const [categoryName, setCategoryName] = useState('');
  const [categoryImage, setCategoryImage] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const apiBaseUrl = process.env.REACT_APP_API_URL;

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const authToken = localStorage.getItem('access');
        if (!authToken) {
          throw new Error('Authentication token not found');
        }

        const headers = {
          'x-auth-token-admin': authToken,
        };

        const response = await axios.get(`${apiBaseUrl}/api/admin/getCategories`, {
          headers,
        });

        const userData = response.data.results.categories;
        setData(userData);
      } catch (error) {
        console.error('Error fetching user data:', error);
        throw error;
      }
    };

    fetchUser();
  }, []);

  const handleNameChange = (event) => {
    setCategoryName(event.target.value);
  };

  const handleImageChange = (event) => {
    setCategoryImage(event.target.files[0]);
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);

    const filteredData = data.filter((item) =>
      item.name.toLowerCase().includes(event.target.value.toLowerCase()) ||
      item.email.toLowerCase().includes(event.target.value.toLowerCase()) ||
      item.number.toString().includes(event.target.value)
    );

    setSearchResults(filteredData);
  };

  const formatDate = (dateString) => {
    const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
    return new Date(dateString).toLocaleDateString('en-GB', options);
  };

  const addCategory = async () => {
    const formData = new FormData();
    formData.append('name', categoryName);
    formData.append('image', categoryImage);

    try {
      const authToken = localStorage.getItem('access');
      const headers = {
        'x-auth-token-admin': authToken,
      };

      const response = await fetch('http://ec2-52-66-186-107.ap-south-1.compute.amazonaws.com:3009/api/admin/newCategory', {
        method: 'POST',
        body: formData,
        headers: headers,
      });

      const responseData = await response.json();

      if (!responseData.error) {
        console.log(responseData.message);
        console.log(responseData.results.newCat);
      } else {
        console.error(responseData.message);
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    addCategory();
  };

  const userList = Array.isArray(data) ? data : [];

  return (
    <>
      <div className="admin_main">
        <DashboardNavbar />

        <div className="admin_panel_data height_adjust">
          <div className="row mx-0">
            <div className="col-12 design_outter_comman shadow mb-4">
              <div className="row comman_header justify-content-between">
                <div className="col">
                  <h2>Add New Category </h2>
                </div>
              </div>
              <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" onSubmit={handleSubmit}>
                <div className="form-group mb-0 col-5">
                  <label htmlFor="categoryName">Category Name</label>
                  <input type="text" name="name" id="categoryName" className="form-control" value={categoryName} onChange={handleNameChange} />
                </div>
                <div className="form-group mb-0 col-5 choose_file position-relative">
                  <span>Category Image</span>
                  <label htmlFor="upload_video"><i className="fal fa-camera me-1"></i>Choose File</label>
                  <input type="file" className="form-control" name="image" id="upload_video" onChange={handleImageChange} />
                </div>
                <div className="form-group mb-0 col-2">
                  <button type="submit" className="comman_btn d-flex w-100"><span>Add</span></button>
                </div>
              </form>
            </div>
            <div className="col-12 design_outter_comman shadow">
              <div className="row comman_header justify-content-between">
                <div className="col">
                  <h2>Category Management</h2>
                </div>
                <div className="col-3">
                  <form className="form-design" action="">
                    <div className="form-group mb-0 position-relative icons_set">
                      <input type="text" className="form-control" placeholder="Search" name="name" id="name" onChange={handleSearchChange} />
                      <i className="far fa-search"></i>
                    </div>
                  </form>
                </div>
              </div>
              <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                <div className="form-group mb-0 col-5">
                  <label htmlFor="">From</label>
                  <input type="date" className="form-control" />
                </div>
                <div className="form-group mb-0 col-5">
                  <label htmlFor="">To</label>
                  <input type="date" className="form-control" />
                </div>
                <div className="form-group mb-0 col-2">
                  <button className="comman_btn d-flex w-100"><span>Search</span></button>
                </div>
              </form>
              <div className="row">
                <div className="col-12 comman_table_design px-0">
                  <div className="table-responsive">
                    <table className="table mb-0">
                      <thead>
                        <tr>
                          <th>S.No.</th>
                          <th>Category image</th>
                          <th>Category Name</th>
                          <th>Added Date</th>
                          <th>Status</th>
                          <th>Action</th>
                        </tr>
                      </thead>
                      <tbody>
                        {searchResults.map((item, index) => (
                          <tr key={index}>
                            <td>{index + 1}</td>
                            <td><img className="table_img" src={item.image} alt="" /></td>
                            <td>{item.name}</td>
                            <td>{formatDate(item.updatedAt)}</td>
                            <td>
                              <div className="check_toggle">{item.status}
                                <input type="checkbox" name="maintance" id="maintance" className="d-none" />
                                <label htmlFor="maintance"></label>
                              </div>
                            </td>
                            <td>
                              <Link to={`/category-view/${item._id}`} className="comman_btn table_viewbtn"><span>View</span></Link>
                              <a className="comman_btn2 table_viewbtn ms-1" href="javascript:;" data-bs-toggle="modal" data-bs-target="#edit"><span>Edit</span></a>
                              <a className="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;"><span>Delete</span></a>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default CategoryManagement;
