import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import DashboardNavbar from './DashboardNavbar';

const CategoryManagement = () => {
    const [data, setdata] = useState([]);
    const [categoryName, setCategoryName] = useState('');
    const [categoryImage, setCategoryImage] = useState(null);
    const [searchInput, setSearchInput] = useState('');
    const [filteredData, setFilteredData] = useState([]);
    const apiBaseUrl = process.env.REACT_APP_API_URL;
    const [selectedCategoryId, setSelectedCategoryId] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);

    const openEditModal = async (categoryId) => {
        try {
            const authToken = localStorage.getItem('access');
            const headers = {
                'x-auth-token-admin': authToken,
            };

            const response = await axios.get(`${apiBaseUrl}/api/admin/getCategoryById/${categoryId}`, {
                headers,
            });

            const categoryData = response.data.results.category;
            setSelectedCategory(categoryData);

            // Open the modal using Bootstrap or your preferred modal library
            // Example: $('#edit').modal('show');
        } catch (error) {
            console.error('Error fetching category data:', error);
        }
    };

    useEffect(() => {
        const fetchUser = async () => {
            try {
                const authToken = localStorage.getItem('access');
                if (!authToken) {
                    throw new Error('Authentication token not found');
                }

                const headers = {
                    'x-auth-token-admin': authToken,
                };

                const response = await axios.get(`${apiBaseUrl}/api/admin/getCategories`, {
                    headers,
                });

                const userData = response.data.results.categories;
                setdata(userData);
                setFilteredData(userData);
            } catch (error) {
                console.error('Error fetching user data:', error);
                throw error;
            }
        };

        fetchUser();
    }, []);

    const handleNameChange = (event) => {
        setCategoryName(event.target.value);
    };

    const handleImageChange = (event) => {
        setCategoryImage(event.target.files[0]);
    };

    const addCategory = async () => {
        const formData = new FormData();
        formData.append('name', categoryName);
        formData.append('image', categoryImage);

        try {
            const authToken = localStorage.getItem('access');
            const headers = {
                'x-auth-token-admin': authToken,
            };

            const response = await fetch(`${apiBaseUrl}/api/admin/newCategory`, {
                method: 'POST',
                body: formData,
                headers: headers,
            });

            const responseData = await response.json();

            if (!responseData.error) {
                console.log(responseData.message);
                console.log(responseData.results.newCat);
            } else {
                console.error(responseData.message);
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        addCategory();
    };

    const handleSearchInputChange = (event) => {
        const input = event.target.value;
        setSearchInput(input);

        const filtered = data.filter(item =>
            item.name.toLowerCase().includes(input.toLowerCase())
        );

        setFilteredData(filtered);
    };

    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'numeric', year: '2-digit' };
        return new Date(dateString).toLocaleDateString('en-GB', options);
    };

    const userList = Array.isArray(filteredData) ? filteredData : [];

    return (
        <>
            <div className="admin_main">
                <DashboardNavbar />
                <div className="admin_panel_data height_adjust">
                    <div className="row mx-0">
                        <div className="col-12 design_outter_comman shadow mb-4">
                            <div className="row comman_header justify-content-between">
                                <div className="col">
                                    <h2>Add New Category </h2>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" onSubmit={handleSubmit}>
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="categoryName">Category Name</label>
                                    <input type="text" name="name" id="categoryName" className="form-control" value={categoryName} onChange={handleNameChange} />
                                </div>
                                <div className="form-group mb-0 col-5 choose_file position-relative">
                                    <span>Category Image</span>
                                    <label htmlFor="upload_video"><i className="fal fa-camera me-1"></i>Choose File</label>
                                    <input type="file" className="form-control" name="image" id="upload_video" onChange={handleImageChange} />
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button type="submit" className="comman_btn d-flex w-100"><span>Add</span></button>
                                </div>
                            </form>
                        </div>
                        <div className="col-12 design_outter_comman shadow">
                            <div className="row comman_header justify-content-between">
                                <div className="col">
                                    <h2>Category Management</h2>
                                </div>
                                <div className="col-3">
                                    <form className="form-design" action="">
                                        <div className="form-group mb-0 position-relative icons_set">
                                            <input
                                                type="text"
                                                className="form-control"
                                                placeholder="Search"
                                                name="name"
                                                id="name"
                                                value={searchInput}
                                                onChange={handleSearchInputChange}
                                            />
                                            <i className="far fa-search"></i>
                                        </div>
                                    </form>
                                </div>
                            </div>
                            <form className="form-design py-4 px-3 help-support-form row align-items-end justify-content-between" action="">
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">From</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-5">
                                    <label htmlFor="">To</label>
                                    <input type="date" className="form-control" />
                                </div>
                                <div className="form-group mb-0 col-2">
                                    <button className="comman_btn d-flex w-100"><span>Search</span></button>
                                </div>
                            </form>
                            <div className="row">
                                <div className="col-12 comman_table_design px-0">
                                    <div className="table-responsive">
                                        <table className="table mb-0">
                                            <thead>
                                                <tr>
                                                    <th>S.No.</th>
                                                    <th>Category image</th>
                                                    <th>Category Name</th>
                                                    <th>Added Date</th>
                                                    <th>Status</th>
                                                    <th>Action</th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {userList.map((item, index) => (
                                                    <tr key={index}>
                                                        <td>{index + 1}</td>
                                                        <td><img className="table_img" src={item.image} alt="" /></td>
                                                        <td>{item.name}</td>
                                                        <td>{formatDate(item.updatedAt)}</td>
                                                        <td>
                                                            <div className="check_toggle">{item.status}
                                                                <input type="checkbox" name="maintance" id="maintance" className="d-none" />
                                                                <label htmlFor="maintance"></label>
                                                            </div>
                                                        </td>
                                                        <td>
                                                            <Link to={`/category-view/${item._id}`} className="comman_btn table_viewbtn"><span>View</span></Link>
                                                            <Link className="comman_btn2 table_viewbtn ms-1" onClick={() => openEditModal(item._id)} data-bs-toggle="modal" data-bs-target="#edit"><span>Edit</span></Link>
                                                            <a className="comman_btn bg-danger table_viewbtn ms-1" href="javascript:;"><span>Delete</span></a>
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="modal fade comman_modal" id="edit" data-bs-backdrop="static" data-bs-keyboard="false" tabIndex="-1" aria-labelledby="staticBackdropLabel" aria-hidden="true">
                    <div className="modal-dialog modal-dialog-centered">
                        <div className="modal-content border-0">
                            <div className="modal-header">
                                <h5 className="modal-title" id="staticBackdropLabel">Edit</h5>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                            </div>
                            <div className="modal-body p-0">
                                {selectedCategory && (
                                    <form className="form-design py-4 px-4 row" action="">
                                        <div className="form-group col-12 choose_file position-relative">
                                            <span>Category Image</span>
                                            <label htmlFor="upload_video"><i className="fal fa-camera me-1"></i>Choose File</label>
                                            <input type="file" className="form-control" value="" name="upload_video" id="upload_video" />
                                        </div>
                                        <div className="form-group col-6">
                                            <label htmlFor="">Category Name</label>
                                            <input type="text" value={selectedCategory.name} className="form-control" />
                                        </div>
                                        <div className="form-group col-6">
                                            <label htmlFor="">Added Date</label>
                                            <input type="text" value={formatDate(selectedCategory.updatedAt)} className="form-control" readOnly />
                                        </div>
                                        <div className="form-group mb-0 col-12 text-center">
                                            <button className="comman_btn d-inline-flex" data-bs-dismiss="modal"><span>Update</span></button>
                                        </div>
                                    </form>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
};

export default CategoryManagement;
